mapper.select:
select affair_id as affairID, affair_name as affairName, system as system, series as series, bu as bu, create_by as createBy, update_by as updateBy, update_time as updateTime from mms_examination_affair where 1=1
and affair_name like CONCAT('%',#{affairName},'%')
and system = #{system}
and series like CONCAT('%',#{series},'%')
and bu = #{bu}

mapper.update:
update mms_examination_affair
affair_name = #{affairName}, system = #{system}, series = #{series}, bu = #{bu}, update_by = #{updateBy}, update_time = now()
where affair_id = #{affairID}
