mapper.select:
(SELECT DISTINCT c.courseName AS courseName, c.courseDimension AS dimensionName, item.itemtype AS type FROM course_table c INNER JOIN hrbp_examination_rule_item item ON c.courseName = item.itemName INNER JOIN base_staff_info base ON base.employeeID = c.employeeID WHERE item.itemName = c.courseName and 1=1
and base.bu = #{bu}
and base.du = #{du}
and base.pdu = #{pdu}
and base.employeeID like '%${employeeID}%'
or base.employeeName like '%${employeeName}%'
) UNION (SELECT DISTINCT t.transactionName AS courseName, t.dimensionName AS dimensionName, item.itemtype AS type FROM transaction_table t INNER JOIN hrbp_examination_rule_item item ON t.transactionName = item.itemName INNER JOIN base_staff_info base ON base.employeeID = t.employeeID WHERE item.itemName = t.transactionName and 1=1
and base.bu = #{bu}
and base.du = #{du}
and base.pdu = #{pdu}
and base.employeeID like '%${employeeID}%'
or base.employeeName like '%${employeeName}%'
)

SELECT source.employeeID AS employeeID, source.employeeName AS employeeName, score.itemName AS parentHeader, source.childHeader as childHeader, score.itemType AS itemtype, source.score AS score, score.pass AS pass, score.credit AS credit FROM ( (SELECT base.employeeID AS employeeID, base.employeeName AS employeeName, base.positionRole AS positionRole, base.rank AS rank, base.bu AS bu, base.du AS du, base.pdu AS pdu, course.transactionName AS parentHeader, course.dimensionName AS childHeader, course.dimensionScore AS score FROM transaction_table course INNER JOIN base_staff_info base ON course.employeeID = base.employeeID) union (SELECT base.employeeID AS employeeID, base.employeeName AS employeeName, base.positionRole AS positionRole, base.rank AS rank, base.bu AS bu, base.du AS du, base.pdu AS pdu, course.courseName AS parentHeader, course.courseDimension AS childHeader, course.courseScore AS score FROM course_table course INNER JOIN base_staff_info base ON course.employeeID = base.employeeID) ) source INNER JOIN ( SELECT ruleItem.bu, ruleItem.roleName, ruleItem.roleLevel, item.itemtype, item.itemName, item.credit, item.pass FROM hrbp_examination_rule_item item INNER JOIN ( SELECT base.bu, role.roleName, rule.roleLevel, rule.ruleId FROM hrbp_special_role role INNER JOIN hrbp_examination_rule rule ON role.roleId = rule.roleId INNER JOIN base_staff_info base ON role.creatorId = base.employeeID ) ruleItem ON ruleItem.ruleId = item.ruleId ) score ON source.bu = score.bu AND source.rank = score.roleLevel AND source.positionRole = score.roleName AND source.parentHeader = score.itemName order by itemType
and source.bu = #{bu}
and source.du = #{du}
and source.pdu = #{pdu}
and source.employeeID like '%${employeeID}%'
or source.employeeName like '%${employeeName}%'
