mapper.select:
(SELECT DISTINCT c.courseName AS courseName, c.courseDimension AS dimensionName, item.itemtype AS type FROM mms_course_table c INNER JOIN mms_examination_rule_item item ON c.courseName = item.itemName INNER JOIN mms_base_staff_info base ON base.employeeID = c.employeeID WHERE item.itemName = c.courseName and 1=1
and base.bu = #{bu}
and base.du = #{du}
and base.pdu = #{pdu}
and base.employeeID like '%${employeeID}%'
or base.employeeName like '%${employeeName}%'
) UNION (SELECT DISTINCT t.transactionName AS courseName, t.dimensionName AS dimensionName, item.itemtype AS type FROM mms_transaction_table t INNER JOIN mms_examination_rule_item item ON t.transactionName = item.itemName INNER JOIN mms_base_staff_info base ON base.employeeID = t.employeeID WHERE item.itemName = t.transactionName and 1=1
and base.bu = #{bu}
and base.du = #{du}
and base.pdu = #{pdu}
and base.employeeID like '%${employeeID}%'
or base.employeeName like '%${employeeName}%'
)

SELECT source.employeeID AS employeeID, source.employeeName AS employeeName, score.itemName AS parentHeader, source.childHeader as childHeader, score.itemType AS itemtype, source.score AS score, score.pass AS pass, score.credit AS credit FROM ( (SELECT base.employeeID AS employeeID, base.employeeName AS employeeName, base.positionRole AS positionRole, base.rank AS rank, base.bu AS bu, base.du AS du, base.pdu AS pdu, course.transactionName AS parentHeader, course.dimensionName AS childHeader, course.dimensionScore AS score FROM mms_transaction_table course INNER JOIN mms_base_staff_info base ON course.employeeID = base.employeeID) union (SELECT base.employeeID AS employeeID, base.employeeName AS employeeName, base.positionRole AS positionRole, base.rank AS rank, base.bu AS bu, base.du AS du, base.pdu AS pdu, course.courseName AS parentHeader, course.courseDimension AS childHeader, course.courseScore AS score FROM mms_course_table course INNER JOIN mms_base_staff_info base ON course.employeeID = base.employeeID) ) source INNER JOIN ( SELECT ruleItem.bu, ruleItem.roleName, ruleItem.roleLevel, item.itemtype, item.itemName, item.credit, item.pass FROM mms_examination_rule_item item INNER JOIN ( SELECT base.bu, role.roleName, rule.roleLevel, rule.ruleId FROM mms_special_role role INNER JOIN mms_examination_rule rule ON role.roleId = rule.roleId INNER JOIN mms_base_staff_info base ON role.creatorId = base.employeeID ) ruleItem ON ruleItem.ruleId = item.ruleId ) score ON source.bu = score.bu AND source.rank = score.roleLevel AND source.positionRole = score.roleName AND source.parentHeader = score.itemName order by itemType
and source.bu = #{bu}
and source.du = #{du}
and source.pdu = #{pdu}
and source.employeeID like '%${employeeID}%'
or source.employeeName like '%${employeeName}%'

SELECT scores.employeeID AS employeeID, scores.employeeName AS employeeName, scores.bu AS bu, scores.costCenter AS costCenter, scores.types AS types, scores.score_time as scoreTime, scores.affair_name AS affairName, scores.series AS series, scores.score AS score, scores.modifier AS modifier, scores.scoresID AS scoresID, scores.nameId AS nameId FROM ( SELECT trandieScore.transaction_dimension_id AS scoresID, trandieScore.employee_id AS employeeID, base.employeeName AS employeeName, base.bu AS bu, base.costCenter AS costCenter, trandieScore.type AS types, MAX(trandieScore.change_time) AS score_time, examination.affair_name AS affair_name, examination.series AS series, sum(trandieScore.score) AS score, basei.employeeName AS modifier, trandieScore.affair_id AS nameId FROM mms_transaction_dimension_score trandieScore INNER JOIN mms_base_staff_info base ON base.employeeID = trandieScore.employee_id INNER JOIN mms_base_staff_info basei ON basei.employeeID = trandieScore.modifier LEFT JOIN mms_examination_affair examination ON examination.affair_id = trandieScore.affair_id LEFT JOIN mms_examination_dimension dimension ON dimension.dimension_id = trandieScore.dimension_id GROUP BY trandieScore.employee_id ,trandieScore.affair_id UNION SELECT testScore.cour_score_id AS scoresID, testScore.employee_id AS employeeID, base.employeeName AS employeeName, base.bu AS bu, base.costCenter AS costCenter, testScore.type AS types, opening.training_time AS training_time, opening.training_name AS training_name, opening.series AS series, testScore.test_score AS score, bases.employeeName AS modifier, testScore.opening_id AS nameId FROM mms_course_test_score testScore INNER JOIN mms_base_staff_info base ON base.employeeId = testScore.employee_id INNER JOIN mms_base_staff_info bases ON bases.employeeID = testScore.modifier LEFT JOIN mms_opening_setup_info opening ON opening.opening_id = testScore.opening_id ) scores
scores.series = #{series}
and scores.types = #{types}
and scores.bu = #{bu}
and scores.affairName = #{affairName}
and scores.employeeName = #{employeeName}
and scores.employeeID = #{employeeID}
AND scores.scoreTime between #{startTime} and #{endTime}

delete.if:
delete from mms_transaction_dimension_score


        employee_id = #{employeeID}


        and  affair_id = #{nameID}

mapper.select:
SELECT transaction_dimension_id AS tranAndDieId, employee_id AS employeeID, affair_id AS affairId, dimension_id AS dimensionId, score AS score, evaluation AS evaluation, evaluator AS evaluator, modifier AS evaluator, change_time AS changeTime, type AS types FROM mms_transaction_dimension_score
employee_id = #{employeeID}
and affair_id = #{nameID}
