mapper.select:
SELECT SUM(CASE WHEN staff_id IS NULL THEN 0 ELSE 1 END) AS mmsNumber, SUM(CASE WHEN backup_staff_id IS NULL THEN 0 ELSE 1 END) AS backupMmsNumber FROM ( SELECT projectId FROM mms_project_information
endTime >= CURRENT_DATE
#{poID}
) po_info LEFT JOIN ( SELECT project_id, staff_id, backup_staff_id FROM mms_po_staff_info ) staff_info ON po_info.projectId = staff_info.project_id

SELECT DISTINCT staff_id FROM ( ( SELECT DISTINCT staff_id, po_role_id FROM ( SELECT projectId FROM mms_project_information
endTime >= CURRENT_DATE
#{poID}
) po_info LEFT JOIN ( SELECT project_id, po_role_id,
staff_id AS staff_id
backup_staff_id AS staff_id
FROM mms_po_staff_info ) staff_info ON po_info.projectId = staff_info.project_id WHERE staff_id IS NOT NULL ) po_staff LEFT JOIN ( SELECT employeeID, employeeName, costCenter FROM mms_base_staff_info ) staff_info ON po_staff.staff_id = staff_info.employeeID LEFT JOIN ( SELECT employeeID, birthdayDate FROM mms_family_information ) age_info ON po_staff.staff_id = age_info.employeeID LEFT JOIN ( SELECT employee_id, opening_id FROM mms_course_test_score ) source_info ON po_staff.staff_id = source_info.employee_id LEFT JOIN ( SELECT opening_id, series, training_name FROM mms_course_setting ) training_info ON source_info.opening_id = training_info.opening_id )
AND staff_id like '%${staffID}%'
AND employeeName like '%${staffName}%'
AND costCenter = #{costCenter}
AND po_role_id = #{post}
AND series = #{series}
AND training_name = #{trainingname}
AND birthdayDate >= #{ageStart}
AND birthdayDate < #{ageEnd}
