SELECT SUM(CASE WHEN po_role.po_role_id IS NULL THEN 0 ELSE 1 END) AS backnonenum, SUM(CASE WHEN po_role.po_role_id IS NULL THEN 1 ELSE 0 END) AS nobacknonenum FROM base_staff_info LEFT JOIN ( SELECT staff_id, po_role_id FROM po_staff_info,po_temp WHERE po_staff_info.project_id =po_temp.poID AND po_temp.endDate >= CURRENT_DATE ) po_role ON base_staff_info.employeeID = po_role.staff_id
base_staff_info.bu = #{bu}
AND base_staff_info.workPlaceArea = #{workPlaceArea}
AND base_staff_info.pdu = #{cu}

SELECT base_staff_info.costCenter AS name, COUNT(*) AS number FROM base_staff_info LEFT JOIN ( SELECT staff_id, po_role_id FROM po_staff_info,po_temp WHERE po_staff_info.project_id =po_temp.poID AND po_temp.endDate >= CURRENT_DATE ) po_role ON base_staff_info.employeeID = po_role.staff_id
po_role.po_role_id IS NOT NULL
po_role.po_role_id IS NULL
AND base_staff_info.bu = #{bu}
AND base_staff_info.workPlaceArea = #{workPlaceArea}
AND base_staff_info.pdu = #{cu}
GROUP BY base_staff_info.costCenter

