courseName AS name,
trainName AS name,
staffID FROM train_temp
staffID IN
#{item}
AND trainName = #{trainName}
) temp GROUP BY name

SELECT SUM(CASE WHEN birthdayDate >= #{age30} THEN 1 ELSE 0 END) AS agelessthan30, SUM(CASE WHEN birthdayDate < #{age30} AND birthdayDate >= #{age40} THEN 1 ELSE 0 END) AS age30to40, SUM(CASE WHEN birthdayDate < #{age40} THEN 1 ELSE 0 END) AS agegreaterthan40 FROM ( SELECT base_staff_info.employeeID AS staff_id FROM base_staff_info LEFT JOIN ( SELECT staff_id, po_role_id FROM po_staff_info,po_temp WHERE po_staff_info.project_id = po_temp.poID AND po_temp.endDate >= CURRENT_DATE ) po_role ON base_staff_info.employeeID = po_role.staff_id
po_role.po_role_id IS NOT NULL
po_role.po_role_id IS NULL
AND base_staff_info.bu = #{bu}
AND base_staff_info.workPlaceArea = #{workPlaceArea}
AND base_staff_info.pdu = #{cu}
) staff_info LEFT JOIN family_information ON staff_info.staff_id =family_information.employeeID

SELECT COUNT(*) AS totleNumber FROM base_staff_info LEFT JOIN ( SELECT staff_id, po_role_id FROM po_staff_info,po_temp WHERE po_staff_info.project_id =po_temp.poID AND po_temp.endDate >= CURRENT_DATE ) po_role ON base_staff_info.employeeID = po_role.staff_id
po_role.po_role_id IS NOT NULL
po_role.po_role_id IS NULL
AND base_staff_info.bu = #{bu}
AND base_staff_info.workPlaceArea = #{workPlaceArea}
AND base_staff_info.pdu = #{cu}
AND base_staff_info.generalPosition = #{post}
AND base_staff_info.employeeID like '%${staffID}%'
AND base_staff_info.employeeName like '%${staffName}%'

SET @r:=-1;
SELECT * FROM ( SELECT @r:=@r+1 as row_num, employeeID AS staffID, employeeName AS staffName, costCenter AS costCenter, sex AS sex, cooperationMode AS cooperationMode, positionRole AS positionRole, employmentDate AS employmentDate, graduationTime AS graduationTime, education AS education, if211 AS if211 FROM base_staff_info LEFT JOIN ( SELECT staff_id, po_role_id FROM po_staff_info,po_temp WHERE po_staff_info.project_id =po_temp.poID AND po_temp.endDate >= CURRENT_DATE ) po_role ON base_staff_info.employeeID = po_role.staff_id
po_role.po_role_id IS NOT NULL
po_role.po_role_id IS NULL
AND base_staff_info.bu = #{bu}
AND base_staff_info.workPlaceArea = #{workPlaceArea}
AND base_staff_info.pdu = #{cu}
AND base_staff_info.generalPosition = #{post}
AND base_staff_info.employeeID like '%${staffID}%'
AND base_staff_info.employeeName like '%${staffName}%'
) TMP WHERE row_num BETWEEN #{rowStart} AND #{rowEnd}

